INSERT INTO `wp_wc_admin_notes` VALUES (16,'wc-admin-store-notice-giving-feedback-2','info','en_US','Give feedback','Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.','info','{}','unactioned','woocommerce-admin','2020-07-10 20:37:02',NULL,0,'plain','',0);
