INSERT INTO `wp_wc_admin_notes` VALUES (14,'wc-admin-coupon-page-moved','update','en_US','Coupon management has moved!','Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.','info','{}','unactioned','woocommerce-admin','2020-07-10 13:29:33',NULL,0,'plain','',0);
