INSERT INTO `wp_wc_admin_notes` VALUES (3,'wc-admin-historical-data','update','en_US','WooCommerce Admin: dados históricos de análise','Para visualizar seus dados históricos de análise, você deve processar seus pedidos e clientes existentes.','info','{}','actioned','woocommerce-admin','2019-11-01 00:21:07',NULL,0,'',NULL,0);
