INSERT INTO `wp_wc_admin_notes` VALUES (14,'wc-admin-coupon-page-moved','update','en_US','Coupon management has moved!','Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.','info','{}','unactioned','woocommerce-admin','2020-07-10 13:29:33',NULL,0,'plain','',0);
INSERT INTO `wp_wc_admin_notes` VALUES (6,'wc-admin-facebook-extension','info','en_US','Venda no Facebook','Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.','thumbs-up','{}','actioned','woocommerce-admin','2020-04-29 19:04:49',NULL,0,'',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (15,'wc-admin-marketing-intro','info','en_US','Connect with your audience','Grow your customer base and increase your sales with marketing tools built for WooCommerce.','info','{}','unactioned','woocommerce-admin','2020-07-10 20:37:02',NULL,0,'plain','',0);
