INSERT INTO `wp_wc_admin_notes` VALUES (13,'wc-admin-new-sales-record','info','en_US','Novo recorde de vendas!','Woohoo, May 6th was your record day for sales! Net Sales was R$954,77 beating the previous record of R$954,77 set on May 6th.','trophy','{\"old_record_date\":\"2020-05-06\",\"old_record_amt\":954.76999999999998181010596454143524169921875,\"new_record_date\":\"2020-05-06\",\"new_record_amt\":954.7700000000000954969436861574649810791015625}','unactioned','woocommerce-admin','2020-05-07 20:43:57',NULL,0,'',NULL,0);
