INSERT INTO `wp_wc_admin_notes` VALUES (1,'wc-admin-welcome-note','info','en_US','New feature(s)','Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!','info','{}','unactioned','woocommerce-admin','2019-10-31 20:36:30',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (2,'wc-admin-wc-helper-connection','info','en_US','Connect to WooCommerce.com','Connect to get important product notifications and updates.','info','{}','unactioned','woocommerce-admin','2019-10-31 20:36:30',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (3,'wc-admin-historical-data','update','en_US','WooCommerce Admin: dados históricos de análise','Para visualizar seus dados históricos de análise, você deve processar seus pedidos e clientes existentes.','info','{}','actioned','woocommerce-admin','2019-11-01 00:21:07',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (4,'wc-admin-mobile-app','info','en_US','Instale o aplicativo Woo','Instale o aplicativo do WooCommerce para gerenciar pedidos, receber notificações de vendas e visualizar as principais métricas - onde quer que você esteja.','phone','{}','unactioned','woocommerce-admin','2019-11-11 03:57:51',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (5,'wc-admin-store-notice-giving-feedback','info','en_US','Avalie sua experiência','Se você gostou do WooCommerce Admin, deixe uma avaliação de 5 estrelas. Agradecemos antecipadamente!','info','{}','unactioned','woocommerce-admin','2020-04-29 19:04:49',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (6,'wc-admin-facebook-extension','info','en_US','Venda no Facebook','Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.','thumbs-up','{}','actioned','woocommerce-admin','2020-04-29 19:04:49',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (7,'wc-admin-store-notice-setting-moved','info','en_US','Procurando pela configuração de notificação da loja?','Agora isso pode ser encontrado no personalizador.','info','{}','unactioned','woocommerce-admin','2020-04-30 01:32:31',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (8,'wc-update-db-reminder','update','en_US','Atualização do banco de dados WooCommerce concluída','A atualização do WooCommerce está concluída. Obrigado por atualizar para a última versão!','info','{}','actioned','woocommerce-core','2020-04-30 22:32:32',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (13,'wc-admin-new-sales-record','info','en_US','Novo recorde de vendas!','Woohoo, May 6th was your record day for sales! Net Sales was R$954,77 beating the previous record of R$954,77 set on May 6th.','trophy','{\"old_record_date\":\"2020-05-06\",\"old_record_amt\":954.76999999999998181010596454143524169921875,\"new_record_date\":\"2020-05-06\",\"new_record_amt\":954.7700000000000954969436861574649810791015625}','unactioned','woocommerce-admin','2020-05-07 20:43:57',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (10,'wc-admin-onboarding-email-marketing','info','en_US','Tips, product updates, and inspiration','We\'re here for you — get tips, product updates, and inspiration straight to your mailbox.','mail','{}','unactioned','woocommerce-admin','2020-05-02 21:11:41',NULL,0);
INSERT INTO `wp_wc_admin_notes` VALUES (12,'wc-admin-usage-tracking-opt-in','info','en_US','Help WooCommerce improve with usage tracking','Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://www.masklife.com.br/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.','info','{}','unactioned','woocommerce-admin','2020-05-07 14:42:53',NULL,0);
